using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Data;
using System.Drawing;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Windows.Forms;
using Bunifu.UI.WinForms;
using HumanResourcesApp.Helpers;
using HumanResourcesApp.Models;
using HumanResourcesApp.Models.DBModels;

namespace HumanResourcesApp
{
    public partial class frm_Login : Form
    {
        Data mydata = new Data();
        Tools mytools  = new Tools();


        string image_Eye = @"";
        string image_EyeHidden = @"";

        public frm_Login()
        {
            InitializeComponent();
        }

        private void btn_login_Click(object sender, EventArgs e)
        {

            DBpersonal dataPersonal = new DBpersonal()
            {
                username = txtbox_name.Text,
                password = txtbox_password.Text,
            };

            ReturnObject ret = new ReturnObject();

            ret = mydata.Login(dataPersonal);
            if (ret.ReturnCode == enums.ReturnCode.Ok)
            {
                string username = txtbox_name.Text;
                string password = txtbox_password.Text;

                localResources.personalInformation = (DBpersonal)ret.objectData;
                this.Hide();
                localResources.frmMain.Show();
            }
            else
            {
                MessageBox.Show(ret.message);
            }
        }

        //şifre gizleme
        private void txtbox_password_TextChanged(object sender, EventArgs e)
        {
            txtbox_password.PasswordChar = '*';
        }

        //control tanımalama
        private void Control(Keys tab, BunifuTextBox txtbox_password)
        {
            throw new NotImplementedException();
        }

        //texbox lar arası tab ile geçiş
        private void txtbox_name_KeyPress(object sender, KeyPressEventArgs e)
        {
            goControl(Keys.Tab, txtbox_password);
        }

        //textboxtan tab ve enter ile giriş yap nesnesi tıklama
        public void goControl(Keys keyData, Control i)
        {

            if (keyData == Keys.Enter)
            {
                Control c = i;
                while (c != null && !c.TabStop)
                {
                    c = this.GetNextControl(c, true);

                    if (c != null)
                    {
                        c.Focus();
                    }

                }

            }

            if (keyData == Keys.Tab)
            {
                Control c = i;
                while (c != null && !c.TabStop)
                {
                    c = this.GetNextControl(c, true);

                    if (c != null)
                    {
                        c.Focus();
                    }

                }
            }
        }

        private void frm_Login_Load(object sender, EventArgs e)
        {
            bunifuImageButton1.Image = mytools.Base64ToImage(image_Eye);
        }

        int showPass = 0;

        private void bunifuImageButton1_Click(object sender, EventArgs e)
        {
            if (showPass == 0)
            {
                txtbox_password.PasswordChar = '\0';
                showPass = 1;
                bunifuImageButton1.Image = mytools.Base64ToImage(image_EyeHidden);
            }
            else
            {
                txtbox_password.PasswordChar = '*';
                showPass = 0;
                bunifuImageButton1.Image = mytools.Base64ToImage(image_Eye);

            }
        }
    }
}

